import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Loader2, Shield, Users, Building } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";

interface SetupStatus {
  hasSuperadmin: boolean;
  totalUsers: number;
}

function TenantSetup({ user }: { user: any }) {
  const [tenantData, setTenantData] = useState({
    name: "",
    domain: "",
    website: ""
  });

  const createTenantMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("/api/tenants", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
      window.location.href = "/";
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createTenantMutation.mutate(tenantData);
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <Building className="h-16 w-16 mx-auto mb-4 text-primary" />
          <CardTitle className="text-2xl">Create Your Business Tenant</CardTitle>
          <CardDescription>
            Set up your business profile to start creating AI chatbots
          </CardDescription>
        </CardHeader>

        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="name">Business Name</Label>
              <Input
                id="name"
                type="text"
                placeholder="Your Business Name"
                value={tenantData.name}
                onChange={(e) => setTenantData(prev => ({ ...prev, name: e.target.value }))}
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="domain">Domain (Optional)</Label>
              <Input
                id="domain"
                type="text"
                placeholder="yourbusiness.com"
                value={tenantData.domain}
                onChange={(e) => setTenantData(prev => ({ ...prev, domain: e.target.value }))}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="website">Website (Optional)</Label>
              <Input
                id="website"
                type="url"
                placeholder="https://yourbusiness.com"
                value={tenantData.website}
                onChange={(e) => setTenantData(prev => ({ ...prev, website: e.target.value }))}
              />
            </div>

            <Button type="submit" className="w-full" disabled={createTenantMutation.isPending}>
              {createTenantMutation.isPending ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Creating Tenant...
                </>
              ) : (
                'Create Tenant'
              )}
            </Button>
          </form>

          <div className="mt-4 pt-4 border-t text-center">
            <Button 
              variant="outline" 
              onClick={() => {
                fetch('/api/logout', { method: 'POST', credentials: 'include' })
                  .then(() => window.location.href = '/');
              }}
              className="w-full"
            >
              Sign Out
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export default function Setup() {
  const { user, isAuthenticated } = useAuth();
  const [promotionSuccess, setPromotionSuccess] = useState(false);

  // Check setup status
  const { data: setupStatus, isLoading: statusLoading } = useQuery<SetupStatus>({
    queryKey: ["/api/setup/status"],
    retry: false,
  });

  // Promote to superadmin mutation
  const promoteMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("/api/setup/promote-superadmin", "POST", {});
    },
    onSuccess: (data) => {
      setPromotionSuccess(true);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/setup/status"] });
    },
  });

  if (statusLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="flex items-center gap-2">
          <Loader2 className="h-6 w-6 animate-spin" />
          <span>Checking setup status...</span>
        </div>
      </div>
    );
  }

  // If user is authenticated but not superadmin, show tenant creation
  if (isAuthenticated && setupStatus?.hasSuperadmin && (user as any)?.role !== 'superadmin') {
    return <TenantSetup user={user} />;
  }

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <Shield className="h-16 w-16 mx-auto mb-4 text-primary" />
          <CardTitle className="text-2xl">Platform Setup</CardTitle>
          <CardDescription>
            {setupStatus?.hasSuperadmin
              ? "Welcome back, Administrator"
              : "Set up your multi-tenant chatbot platform"
            }
          </CardDescription>
        </CardHeader>

        <CardContent className="space-y-4">
          {!isAuthenticated && (
            <div className="text-center">
              <p className="text-sm text-muted-foreground mb-4">
                Please sign in to continue with the setup process.
              </p>
              <Button 
                onClick={() => window.location.href = '/api/login'}
                className="w-full"
              >
                Sign In
              </Button>
            </div>
          )}

          {isAuthenticated && !setupStatus?.hasSuperadmin && !promotionSuccess && (
            <div className="space-y-4">
              <Alert>
                <Users className="h-4 w-4" />
                <AlertDescription>
                  You're the first user! Become the platform administrator to manage tenants and users.
                </AlertDescription>
              </Alert>

              <div className="text-sm text-muted-foreground space-y-2">
                <p><strong>As a superadmin, you'll be able to:</strong></p>
                <ul className="list-disc list-inside ml-4 space-y-1">
                  <li>Create and manage business tenants</li>
                  <li>View platform-wide analytics</li>
                  <li>Manage user roles and permissions</li>
                  <li>Monitor system performance</li>
                </ul>
              </div>

              <Button 
                onClick={() => promoteMutation.mutate()}
                disabled={promoteMutation.isPending}
                className="w-full"
              >
                {promoteMutation.isPending ? (
                  <>
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                    Setting up...
                  </>
                ) : (
                  <>
                    <Shield className="h-4 w-4 mr-2" />
                    Become Platform Administrator
                  </>
                )}
              </Button>
            </div>
          )}

          {promotionSuccess && (
            <div className="text-center space-y-4">
              <Alert className="border-green-200 bg-green-50 text-green-800">
                <Shield className="h-4 w-4" />
                <AlertDescription>
                  Successfully set up as platform administrator!
                </AlertDescription>
              </Alert>

              <Button 
                onClick={() => window.location.href = '/'}
                className="w-full"
              >
                Go to Dashboard
              </Button>
            </div>
          )}

          {setupStatus?.hasSuperadmin && (user as any)?.role === 'superadmin' && (
            <div className="text-center space-y-4">
              <Alert className="border-blue-200 bg-blue-50 text-blue-800">
                <Shield className="h-4 w-4" />
                <AlertDescription>
                  You are signed in as platform administrator.
                </AlertDescription>
              </Alert>

              <Button 
                onClick={() => window.location.href = '/'}
                className="w-full"
              >
                Go to Dashboard
              </Button>
            </div>
          )}

          {promoteMutation.isError && (
            <Alert className="border-red-200 bg-red-50 text-red-800">
              <AlertDescription>
                {promoteMutation.error instanceof Error 
                  ? promoteMutation.error.message 
                  : "Failed to set up administrator. Please try again."}
              </AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/chatbot/ChatWidget.content:
import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Bot, X, Send, Minimize2 } from "lucide-react";
import { cn } from "@/lib/utils";

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface ChatWidgetProps {
  chatbotId: string;
  config?: {
    name?: string;
    welcomeMessage?: string;
    theme?: {
      primaryColor?: string;
      backgroundColor?: string;
      textColor?: string;
    };
  };
  className?: string;
}

export default function ChatWidget({ chatbotId, config, className }: ChatWidgetProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId] = useState(() => crypto.randomUUID());
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const theme = config?.theme || {
    primaryColor: '#6366F1',
    backgroundColor: '#FFFFFF',
    textColor: '#000000'
  };

  useEffect(() => {
    if (isOpen && messages.length === 0) {
      // Add welcome message
      const welcomeMessage: Message = {
        id: crypto.randomUUID(),
        role: 'assistant',
        content: config?.welcomeMessage || "Hi! How can I help you today?",
        timestamp: new Date()
      };
      setMessages([welcomeMessage]);
    }
  }, [isOpen, config?.welcomeMessage]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const sendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: Message = {
      id: crypto.randomUUID(),
      role: 'user',
      content: inputValue,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue("");
    setIsLoading(true);

    try {
      const response = await fetch(`/api/public/chat/${chatbotId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: inputValue,
          sessionId
        })
      });

      if (!response.ok) {
        throw new Error('Failed to send message');
      }

      const data = await response.json();

      const assistantMessage: Message = {
        id: crypto.randomUUID(),
        role: 'assistant',
        content: data.response,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage: Message = {
        id: crypto.randomUUID(),
        role: 'assistant',
        content: "I'm sorry, I'm having trouble responding right now. Please try again later.",
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  if (!isOpen) {
    return (
      <div className={cn("fixed bottom-6 right-6 z-50", className)}>
        <Button
          onClick={() => setIsOpen(true)}
          className="w-14 h-14 rounded-full shadow-lg transition-all duration-300 hover:scale-110"
          style={{ backgroundColor: theme.primaryColor }}
        >
          <Bot className="w-6 h-6 text-white" />
        </Button>
      </div>
    );
  }

  return (
    <div className={cn("fixed bottom-6 right-6 z-50 w-80", className)}>
      <Card 
        className="shadow-2xl border-0 overflow-hidden transition-all duration-300"
        style={{ backgroundColor: theme.backgroundColor }}
      >
        {/* Header */}
        <CardHeader 
          className="p-4 text-white flex flex-row items-center justify-between space-y-0"
          style={{ backgroundColor: theme.primaryColor }}
        >
          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center">
              <Bot className="w-4 h-4" />
            </div>
            <span className="font-medium">{config?.name || 'AI Assistant'}</span>
          </div>
          <div className="flex items-center space-x-1">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsMinimized(!isMinimized)}
              className="w-8 h-8 p-0 text-white/80 hover:text-white hover:bg-white/10"
            >
              <Minimize2 className="w-4 h-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsOpen(false)}
              className="w-8 h-8 p-0 text-white/80 hover:text-white hover:bg-white/10"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        </CardHeader>

        {/* Messages */}
        {!isMinimized && (
          <>
            <CardContent className="p-0">
              <div className="h-80 overflow-y-auto p-4 space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={cn(
                      "flex",
                      message.role === 'user' ? 'justify-end' : 'justify-start'
                    )}
                  >
                    {message.role === 'assistant' && (
                      <div 
                        className="w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 mr-2 mt-1"
                        style={{ backgroundColor: theme.primaryColor }}
                      >
                        <Bot className="w-3 h-3 text-white" />
                      </div>
                    )}
                    <div
                      className={cn(
                        "max-w-xs p-3 rounded-lg text-sm",
                        message.role === 'user'
                          ? "text-white"
                          : "bg-gray-100"
                      )}
                      style={{
                        backgroundColor: message.role === 'user' ? theme.primaryColor : undefined,
                        color: message.role === 'assistant' ? theme.textColor : undefined
                      }}
                    >
                      <p>{message.content}</p>
                    </div>
                  </div>
                ))}
                {isLoading && (
                  <div className="flex justify-start">
                    <div 
                      className="w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 mr-2 mt-1"
                      style={{ backgroundColor: theme.primaryColor }}
                    >
                      <Bot className="w-3 h-3 text-white" />
                    </div>
                    <div className="bg-gray-100 p-3 rounded-lg">
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </div>
            </CardContent>

            {/* Input */}
            <div className="p-3 border-t border-gray-200">
              <div className="flex items-center space-x-2">
                <Input
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message..."
                  className="flex-1 border-gray-300 focus:ring-2"
                  style={{ 
                    color: theme.textColor,
                    borderColor: '#d1d5db'
                  }}
                  disabled={isLoading}
                />
                <Button
                  onClick={sendMessage}
                  disabled={!inputValue.trim() || isLoading}
                  className="p-2"
                  style={{ backgroundColor: theme.primaryColor }}
                >
                  <Send className="w-4 h-4 text-white" />
                </Button>
              </div>
            </div>
          </>
        )}
      </Card>
    </div>
  );
}

client/src/components/chatbot/FlowBuilde.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit, Trash2, MessageSquare, ArrowRight, Save } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface FlowNode {
  id: string;
  type: 'message' | 'question' | 'condition' | 'action';
  content: string;
  responses?: FlowResponse[];
  nextNode?: string;
  conditions?: FlowCondition[];
  action?: string;
}

interface FlowResponse {
  id: string;
  text: string;
  nextNode?: string;
}

interface FlowCondition {
  id: string;
  condition: string;
  nextNode?: string;
}

interface FlowBuilderProps {
  chatbot: any;
  onUpdate: (flows: any) => void;
}

export default function FlowBuilder({ chatbot, onUpdate }: FlowBuilderProps) {
  const { toast } = useToast();
  const [flows, setFlows] = useState<FlowNode[]>(chatbot.flows?.nodes || []);
  const [selectedNode, setSelectedNode] = useState<FlowNode | null>(null);
  const [isEditing, setIsEditing] = useState(false);

  const getNodeTypeColor = (type: string) => {
    switch (type) {
      case 'message': return 'bg-blue-500/10 text-blue-400';
      case 'question': return 'bg-green-500/10 text-green-400';
      case 'condition': return 'bg-yellow-500/10 text-yellow-400';
      case 'action': return 'bg-purple-500/10 text-purple-400';
      default: return 'bg-gray-500/10 text-gray-400';
    }
  };

  const createNewNode = (type: FlowNode['type']) => {
    const newNode: FlowNode = {
      id: crypto.randomUUID(),
      type,
      content: '',
      responses: type === 'question' ? [] : undefined,
      conditions: type === 'condition' ? [] : undefined,
      action: type === 'action' ? '' : undefined,
    };

    setFlows(prev => [...prev, newNode]);
    setSelectedNode(newNode);
    setIsEditing(true);
  };

  const updateNode = (nodeId: string, updates: Partial<FlowNode>) => {
    setFlows(prev =>
      prev.map(node => node.id === nodeId ? { ...node, ...updates } : node)
    );

    if (selectedNode?.id === nodeId) {
      setSelectedNode(prev => prev ? { ...prev, ...updates } : null);
    }
  };

  const deleteNode = (nodeId: string) => {
    setFlows(prev => prev.filter(node => node.id !== nodeId));
    if (selectedNode?.id === nodeId) {
      setSelectedNode(null);
      setIsEditing(false);
    }
  };

  const addResponse = (nodeId: string) => {
    const newResponse: FlowResponse = {
      id: crypto.randomUUID(),
      text: '',
      nextNode: undefined,
    };

    updateNode(nodeId, {
      responses: [...(flows.find(n => n.id === nodeId)?.responses || []), newResponse]
    });
  };

  const updateResponse = (nodeId: string, responseId: string, updates: Partial<FlowResponse>) => {
    const node = flows.find(n => n.id === nodeId);
    if (!node?.responses) return;

    const updatedResponses = node.responses.map(resp =>
      resp.id === responseId ? { ...resp, ...updates } : resp
    );

    updateNode(nodeId, { responses: updatedResponses });
  };

  const removeResponse = (nodeId: string, responseId: string) => {
    const node = flows.find(n => n.id === nodeId);
    if (!node?.responses) return;

    const updatedResponses = node.responses.filter(resp => resp.id !== responseId);
    updateNode(nodeId, { responses: updatedResponses });
  };

  const saveFlows = () => {
    const flowData = {
      nodes: flows,
      startNode: flows[0]?.id || null,
      version: Date.now()
    };

    onUpdate(flowData);

    toast({
      title: "Success",
      description: "Conversation flows saved successfully",
    });
  };

  return (
    <div className="space-y-6">
      {/* Flow Builder Header */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="text-white">Conversation Flow Builder</CardTitle>
              <CardDescription className="text-gray-400">
                Design conversation paths and responses for your chatbot
              </CardDescription>
            </div>
            <div className="flex items-center space-x-2">
              <Button 
                onClick={saveFlows}
                className="bg-primary hover:bg-primary/90"
              >
                <Save className="w-4 h-4 mr-2" />
                Save Flows
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2">
            <Button
              onClick={() => createNewNode('message')}
              variant="outline"
              size="sm"
              className="border-blue-500/20 text-blue-400 hover:bg-blue-500/10"
            >
              <Plus className="w-4 h-4 mr-2" />
              Message
            </Button>
            <Button
              onClick={() => createNewNode('question')}
              variant="outline"
              size="sm"
              className="border-green-500/20 text-green-400 hover:bg-green-500/10"
            >
              <Plus className="w-4 h-4 mr-2" />
              Question
            </Button>
            <Button
              onClick={() => createNewNode('condition')}
              variant="outline"
              size="sm"
              className="border-yellow-500/20 text-yellow-400 hover:bg-yellow-500/10"
            >
              <Plus className="w-4 h-4 mr-2" />
              Condition
            </Button>
            <Button
              onClick={() => createNewNode('action')}
              variant="outline"
              size="sm"
              className="border-purple-500/20 text-purple-400 hover:bg-purple-500/10"
            >
              <Plus className="w-4 h-4 mr-2" />
              Action
            </Button>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Flow Nodes List */}
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Flow Nodes</CardTitle>
            <CardDescription className="text-gray-400">
              Manage your conversation flow components
            </CardDescription>
          </CardHeader>
          <CardContent>
            {flows.length === 0 ? (
              <div className="text-center py-8">
